using HomeKitDotNet.Crypto;
using System.Numerics;

namespace Test
{
    [TestClass]
    public sealed class SRPTest
    {
        BigInteger a = new BigInteger(Convert.FromHexString("60975527035CF2AD1989806F0407210BC81EDC04E2762A56AFD529DDDA2D4393"), true, true);
        BigInteger A = new BigInteger(Convert.FromHexString(""), true, true);
        BigInteger B = new BigInteger(Convert.FromHexString(""), true, true);
        byte[] s = Convert.FromHexString("BEB25379D1A8581EB5A727673A2441EE");
        BigInteger S = new BigInteger(Convert.FromHexString(""), true, true);
        BigInteger K = new BigInteger(Convert.FromHexString("5CBC219DB052138EE1148C71CD4498963D682549CE91CA24F098468F06015BEB6AF245C2093F98C3651BCA83AB8CAB2B580BBF02184FEFDF26142F73DF95AC50"), true, true);

        [TestMethod]
        public void SRPTest_GenA()
        {
            BigInteger result = new SRP().GenerateAValues(a);
            Assert.AreEqual(A, result);
        }

        [TestMethod]
        public void SRPTest_PremasterSecret()
        {
            SRP srp = new SRP();
            srp.GenerateAValues(a);

            BigInteger result = srp.ComputePremasterSecret("alice", "password123", s, B);
            Assert.AreEqual(S, result);
        }

        [TestMethod]
        public void SRPTest_SessionKey()
        {
            SRP srp = new SRP();
            srp.GenerateAValues(a);

            BigInteger result = srp.ComputeSessionKey("alice", "password123", s, B);
            Assert.AreEqual(K, result);
        }
    }
}
